; (function ($) {
    "use strict";


	/* --------  Avoid `console` errors in browsers that lack a console. ------- */
	(function() {
		var method;
		var noop = function () {};
		var methods = [
		'assert', 'clear', 'count', 'debug', 'dir', 'dirxml', 'error',
		'exception', 'group', 'groupCollapsed', 'groupEnd', 'info', 'log',
		'markTimeline', 'profile', 'profileEnd', 'table', 'time', 'timeEnd',
		'timeline', 'timelineEnd', 'timeStamp', 'trace', 'warn'
		];
		var length = methods.length;
		var console = (window.console = window.console || {});

		while (length--) {
			method = methods[length];

	        // Only stub undefined methods.
	        if (!console[method]) {
	        	console[method] = noop;
	        }
	    }
	}());



	jQuery(document).ready(function() {
	  jQuery(".navbar-nav li.menu-item-has-children a").click(function() {
	    jQuery(".navbar-nav li.menu-item-has-children a").not(this).next("ul.sub-menu").slideUp(0, '');
	    jQuery(this).next("ul.sub-menu").slideToggle("fast", '');
	  });

		// If fixed-top class exists then low down breadcrumbs
		if ($("#masthead").hasClass("fixed-top")) {
		  $(".polmo-lite-page").css('padding-top','80px');
		  $(".main-content").css('padding-top','120px');
		  $(".navbar-nav li:hover .sub-menu, .navbar-nav li:focus .sub-menu, .navbar-nav li:focus-within .sub-menu").css('top','72px !important');
		}

	});


	jQuery(window).on('scroll', function($){
		"use strict";

		if (jQuery(window).scrollTop() > 300) {
			jQuery('.fixed-top').addClass('bg-change animated');
		}
		else {
			jQuery('.fixed-top').removeClass('bg-change animated');
		}

		/*------------- Scroll to Top -----------------*/

		if (jQuery(this).scrollTop() > 100) {
			jQuery('#scroll-to-top').fadeIn('slow');
		} else {
			jQuery('#scroll-to-top').fadeOut('slow');
		}
	});


	jQuery('#scroll-to-top').click(function(){
		jQuery("html,body").animate({ scrollTop: 0 }, 1500);
		return false;
	});




})(jQuery);